"use strict";

var genfun = require('./');

var d = genfun.formats.d;

function multiply(a, b) {
  return a * b;
}

function addAndMultiplyNumber(val) {
  var fn = genfun("\n    function (n) {\n      if (typeof n !== 'number') {\n        throw new Error('argument should be a number')\n      }\n      const result = multiply(".concat(d(val), ", n + ").concat(d(val), ")\n      return result\n    }\n  ")); // use fn.toString() if you want to see the generated source

  return fn.toFunction({
    multiply: multiply
  });
}

var addAndMultiply2 = addAndMultiplyNumber(2);
console.log(addAndMultiply2.toString());
console.log('(3 + 2) * 2 =', addAndMultiply2(3));